  #{isEnabled,jdbcType=TINYINT}, #{manufacturer,jdbcType=VARCHAR}, #{brand,jdbcType=VARCHAR}, 
  #{productDetailPath,jdbcType=VARCHAR}, #{opId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}
  )

